import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends Component {
  constructor() {
    super();
    this.state = {
      mesajeleAuSosit: false,
      message_index: -1
    }
  }
  componentDidMount() {
    axios.get('http://localhost:4000/messages')
      .then(response => {
        this.setState({
          mesajeleAuSosit: true,
          messages: response.data
        })
      })
      .catch(eroare => console.log(eroare))
  }
  updateData = (event) => {
    this.setState({
      [event.target.id]: event.target.value
    })
  }
  sendMessage = () => {
    if (this.state.message_index== -1) {
      axios.post('http://localhost:4000/send_message', {
        author: this.state.author,
        message: this.state.message
      })
        .then(response => {
          axios.get('http://localhost:4000/messages')
            .then(response => {
              this.setState({
                mesajeleAuSosit: true,
                messages: response.data
              })
            })
            .catch(eroare => console.log(eroare))
        })
        .catch(error => console.log(error));
    } else {
      axios.post('http://localhost:4000/reply_message', {
        author: this.state.author,
        message: this.state.message,
        message_index: this.state.message_index
      })
        .then(response => {
          axios.get('http://localhost:4000/messages')
            .then(response => {
              this.setState({
                mesajeleAuSosit: true,
                messages: response.data
              })
            })
            .catch(eroare => console.log(eroare))
        })
        .catch(error => console.log(error));
    }
  }
  render() {
    return (
      <div>
        <input id="user" />
      </div>
    );
  }
}
export default App;
