import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import axios from "axios";
class App extends Component {
  constructor() {
    super();
    this.state = {
      mesajeleAuSosit: false
    };
  }
  componentDidMount() {
    axios
      .get("http://localhost:4000/messages")
      .then(response => {
        this.setState({
          mesajeleAuSosit: true,
          messages: response.data
        });
      })
      .catch(eroare => console.log(eroare));
  }
  updateData = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  };
  sendMessage = () => {
    axios
      .post("http://localhost:4000/send_message", {
        author: this.state.author,
        message: this.state.message
      })
      .then(response => {
        axios
          .get("http://localhost:4000/messages")
          .then(response => {
            this.setState({
              mesajeleAuSosit: true,
              messages: response.data
            });
          })
          .catch(eroare => console.log(eroare));
      })
      .catch(error => console.log(error));
  };
  render() {
    return (
      <div>
        <h4>Lista cu mesaje</h4>
        <div>
          {this.state.mesajeleAuSosit ? (
            this.state.messages.map(x => {
              return (
                <div className="message">
                  <h6>{x.author}</h6>
                  <span>{x.message}</span>
                  <button className="reply">Reply</button>
                  {x.responses ? (
                    <div>
                      {x.responses.map(y => {
                        return (
                          <div className="response">
                            {y.author}
                            <span>{y.message}</span>
                          </div>
                        );
                      })}
                    </div>
                  ) : null}
                </div>
              );
            })
          ) : (
            <div>Se asteapta datele de la server</div>
          )}
        </div>
        <div>
          <input
            value={this.state.author}
            onChange={this.updateData}
            id="author"
            placeholder="Author, your name"
          />
          <br />
          <input
            value={this.state.message}
            onChange={this.updateData}
            id="message"
            placeholder="message"
          />
          <br />
          <button onClick={this.sendMessage}>Send Message</button>
        </div>
      </div>
    );
  }
}
export default App;
