import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
class App extends Component {
  constructor() {
    super();
    this.state = {
      mesajeleAuSosit: false
    }
  }
  componentDidMount() {
    axios.get('http://localhost:4000/messages')
    .then(response => {
      this.setState({
        mesajeleAuSosit: true,
        messages: response.data
      })
    })
    .catch(eroare => console.log(eroare))
  }
  render() {
    return (
      <div className="App">
        <h4>Lista cu mesaje</h4>
        <div>
        {
          this.state.mesajeleAuSosit ?
          this.state.messages.map(x => {
            return (<div className="message">
              <h6>{x.author}</h6>
              <span>{x.message}</span>
            </div>)
          })
          : (<div>Se asteapta datele de la server</div>)
        }
        </div>
      </div>
    );
  }
}
export default App;
