import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import crypto from 'crypto';
function checksum(str, algorithm, encoding) {
  return crypto
    .createHash(algorithm || 'md5')
    .update(str, 'utf8')
    .digest(encoding || 'hex')
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      username: "",
      password: "",
      loginForm: true
    }
    this.message_status = React.createRef();
  }
  updateData = (event) => {
    this.setState({
      [event.target.id]: event.target.value
    })
  }
  tryLogin = () => {
    //alert(checksum(this.state.password, "sha1"))
    axios.post('http://localhost:4000/login', {
      username: this.state.username,
      password: this.state.password
    })
      .then((response) => {
        this.message_status.current.innerHTML = "Success"
      })
      .catch((response) => {
        this.message_status.current.innerHTML = "Eroare"
      })
  }
  LoginForm = () => {
    return (
      <>
        <h4>Login Form</h4>
        <input value={this.state.username} onChange={this.updateData}
          id="username" placeholder="Username" />
        <input value={this.state.password} onChange={this.updateData}
          id="password" placeholder="Password" type="password" />
        <button onClick={this.tryLogin}>Login</button>
        <label ref={this.message_status}></label>
      </>
    )
  }
  render() {
    return (
      <div className="App">
        {
          this.state.loginForm ?
            <this.LoginForm/> : null
        }
      </div>
    );
      }
    }

    export default App;
